/*
 * help related
 */

std::vector<nlohmann::fifo_map<std::string, std::string>> read_k8slbrp_ports_list_by_id_get_list(const std::string &name) {
  std::vector<nlohmann::fifo_map<std::string, std::string>> r;
  auto &&k8slbrp = get_cube(name);

  auto &&ports = k8slbrp->getPortsList();
  for(auto &i : ports) {
    nlohmann::fifo_map<std::string, std::string> keys;

    keys["name"] = i->getName();

    r.push_back(keys);
  }
  return r;
}

std::vector<nlohmann::fifo_map<std::string, std::string>> read_k8slbrp_service_backend_list_by_id_get_list(const std::string &name, const std::string &vip, const uint16_t &vport, const ServiceProtoEnum &proto) {
  std::vector<nlohmann::fifo_map<std::string, std::string>> r;
  auto &&k8slbrp = get_cube(name);
  auto &&service = k8slbrp->getService(vip, vport, proto);

  auto &&backend = service->getBackendList();
  for(auto &i : backend) {
    nlohmann::fifo_map<std::string, std::string> keys;

    keys["ip"] = i->getIp();

    r.push_back(keys);
  }
  return r;
}

std::vector<nlohmann::fifo_map<std::string, std::string>> read_k8slbrp_service_list_by_id_get_list(const std::string &name) {
  std::vector<nlohmann::fifo_map<std::string, std::string>> r;
  auto &&k8slbrp = get_cube(name);

  auto &&service = k8slbrp->getServiceList();
  for(auto &i : service) {
    nlohmann::fifo_map<std::string, std::string> keys;

    keys["vip"] = i->getVip();
    keys["vport"] = std::to_string(i->getVport());
    keys["proto"] = ServiceJsonObject::ServiceProtoEnum_to_string(i->getProto());

    r.push_back(keys);
  }
  return r;
}


